import React, { Component } from 'react';
import Table from 'react-bootstrap/Table';

class DataTable extends Component{

	constructor(props){
		super(props);
		this.state={
			data:[]
		}
	}

	componentDidMount() {
		fetch("https://api.covid19india.org/data.json")
		.then(response=>response.json())
		.then((item)=>{
			this.setState({data:item.statewise})
		})
		.catch(err=>console.log(err)) 

	}


	  render(){
		setInterval(this.componentDidMount,1000)

		this.state.data.shift();

		  return(
			<div className="ma5">
				<p className="f3 baskerville ml2 mt2 white">Cases from each State*</p>
			<Table striped bordered hover variant="dark">
		<thead>
			<tr>
			<th>State</th>
			<th>Confirmed</th>
			<th>Active</th>
			<th>Recovered</th>
			<th>Deaths</th>
			</tr>
		</thead>
		<tbody>
		{this.state.data.map((item, key) => {
          return (
          <tr key={key}>
            <td>{item.state}</td>
            <td>{item.confirmed}</td>
            <td>{item.active}</td>
			<td>{item.recovered}</td>
			<td>{item.deaths}</td>
          </tr>
          )
       })}
		</tbody>
		</Table>
		<p className="f5 ml2 mb2 white">*As per data from <a href='https://www.covid19india.org/' target="_blank" >Covid 19 India</a></p>
		</div>
		  )
	  }

}


export default DataTable;
